import Button from "../button/Button";
import './card.css'

const Card = ({language, img}) =>{
    return (
        <>
        <div className="wrapper">
            <h2>Language: {language}</h2>
        </div>
        <img className="images" src={img} alt="language" />
        <Button/>
        </>
    )
}
export default Card;
